/* Three.js loader stub (r128). Loads the CDN build if THREE is not present. */
(() => {
    if (window.THREE) {
        window.dispatchEvent(new Event('threejs:ready'));
        return;
    }

    const script = document.createElement('script');
    script.src = 'https://unpkg.com/three@0.128.0/build/three.min.js';
    script.async = true;
    script.onload = () => {
        window.dispatchEvent(new Event('threejs:ready'));
    };
    script.onerror = () => {
        console.error('Failed to load Three.js from CDN.');
    };
    document.head.appendChild(script);
})();
